import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import { useState } from "react";

function Counter() {
  const [count, setCount] = useState(0);
  const [color, changeColor] = useState("blue");

  const decrement = () => {
    const decrementedCount = count - 1;
    setCount(decrementedCount);
  };

  const increment = () => {
    const incrementedCount = count + 1;
    setCount(incrementedCount);
  };

  const reset = () => {
    setCount(0);
  };

  // const handleChange = (event) => {
  //   const color = event.target.value;
  //   changeColor(color);
  // };

  return (
    <div style={{ backgroundColor: { color } }}>
      <h2>現在のカウント{count}</h2>
      <button onClick={() => decrement()}>-1</button>
      <button onClick={() => increment()}>+1</button>
      <button onClick={() => reset()}>リセット</button>
      {/* <form>
        <label>Color:</label>
        <input type="text" value={color} onChange={handleChange(this)}></input>
        <input type="submit" value="Submit"></input>
      </form> */}
    </div>
  );
}

function App() {
  return (
    <div>
      <Counter></Counter>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
