import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import reportWebVitals from "./reportWebVitals";
import { useState } from "react";

class Product {
  constructor(id, name, price, stock) {
    this.id = id;
    this.name = name;
    this.price = price;
    this.stock = stock;
  }

  getDescription() {
    return this.isAvailable
      ? `${this.name}:${this.price}円:${this.stock}個`
      : `${this.name}:${this.price}円：品切れ`;
  }

  isAvailable() {
    return this.stock > 0;
  }

  getPriceWithTax() {
    return this.price * 1.1;
  }

  getDiscountPrice(percent) {
    return this.price * (1 - percent / 100);
  }
}

function ProductCard(props) {
  const [product, setProduct] = useState(props.initialProduct);
  const [count, setCount] = useState(0);

  const addToCart = () => {
    const updatedProduct = new Product( //新たなオブジェクトを作らないと再レンダリングがされない。
      product.id,
      product.name,
      product.price,
      product.stock - count
    );
    setProduct(updatedProduct);
    console.log(product.getDescription());
  };

  return (
    <div style={{ border: "1px solid gray", margin: "10px", padding: "10px" }}>
      <h2>{product.getDescription()}</h2>
      {product.isAvailable() ? (
        <>
          <p>個数選択</p>
          <select
            value={count}
            onChange={(e) => setCount(Number(e.target.value))}
          >
            {(() => {
              const options = [];
              for (let i = 1; i <= product.stock; i++) {
                options.push(
                  <option key={i} value={i}>
                    {i}個
                  </option>
                );
              }
              return options;
            })()}
          </select>

          <button onClick={addToCart}>買い物かごに追加</button>
        </>
      ) : (
        <p>売り切れです。</p>
      )}
    </div>
  );
}

function App() {
  return (
    <div>
      <ProductCard initialProduct={new Product(0, "消しゴム", 120, 25)} />
      <ProductCard initialProduct={new Product(1, "鉛筆", 300, 5)} />
      <ProductCard
        initialProduct={new Product(2, "マッチ", 200, 0)}
      ></ProductCard>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
